import { useState } from "react";
import { ActorsItem } from "./ActorsItem";

export function ActorsList() {
  let [allActors, setAllActors] = useState([
    {
      actorId: 201,
      actorName: "SK",
      actorMovie: "Amaran",
      actorImage:
        "data:image/webp;base64,",
    },
    {
      actorId: 202,
      actorName: "Asif Ali",
      actorMovie: "Rekkachitram",
      actorImage:
        "data:image/webp;base64,",
    },
    {
      actorId: 203,
      actorName: "SRK",
      actorMovie: "Chennai Express",
      actorImage:
        "data:image/webp;base64,",
    },
  ]);
  let mappedAllActors = allActors.map((eachActor) => (
    <ActorsItem
      key={eachActor.actorId}
      item={eachActor}
      removeFn={(actorId) => deleteFruit(actorId)}
    ></ActorsItem>
  ));
  function deleteFruit(aid) {
    setAllActors(allActors.filter((eachActor) => eachActor.actorId != aid));
  }
  return (
    <>
      <div className="container m-5">
        <h2>List of Actors</h2>
        {allActors.length == 0 ? (
          <div className="alert alert-danger"> No Actors to Display!</div>
        ) : (
          <table className="table table-striped">
            <thead className="table-dark">
              <tr>
                <th>ACTOR ID</th>
                <th>ACTOR IMAGE</th>
                <th>ACTOR NAME</th>
                <th>ACTOR MOVIE</th>
                <th>REMOVE</th>
              </tr>
            </thead>
            <tbody>{mappedAllActors}</tbody>
          </table>
        )}
      </div>
    </>
  );
}
